function toggleLanguageMenu() {
    let menu = document.getElementById("language-menu");
    menu.style.display = (menu.style.display === "block") ? "none" : "block";
}

function changeLanguage(lang) {
    alert("Выбран язык: " + lang);
}

function filterLanguages() {
    let input = document.getElementById("language-search").value.toLowerCase();
    let items = document.querySelectorAll("#language-list li");
    items.forEach(li => {
        if (li.innerText.toLowerCase().includes(input)) {
            li.style.display = "block";
        } else {
            li.style.display = "none";
        }
    });
}

// Корзина
let cartCount = 0;
function addToCart() {
    cartCount++;
    document.getElementById("cart-count").innerText = cartCount;
}
