document.addEventListener("DOMContentLoaded", function() {
    const languageSelect = document.getElementById("language");
    if (languageSelect) {
        languageSelect.addEventListener("change", function() {
            localStorage.setItem("selectedLanguage", this.value);
            location.reload();
        });

        const savedLanguage = localStorage.getItem("selectedLanguage") || "sk";
        languageSelect.value = savedLanguage;

        translatePage(savedLanguage);
    }
});

function translatePage(lang) {
    const translations = {
        "sk": { "title": "Forever Wellness Hub", "cart": "Košík", "profile": "Profil" },
        "ru": { "title": "Форевер Здоровье", "cart": "Корзина", "profile": "Профиль" },
        "de": { "title": "Forever Wellness Hub", "cart": "Warenkorb", "profile": "Profil" },
        "en": { "title": "Forever Wellness Hub", "cart": "Cart", "profile": "Profile" }
    };

    document.title = translations[lang]["title"];
    document.querySelector("a[href='cart.html']").innerText = translations[lang]["cart"];
    document.querySelector("a[href='profile.html']").innerText = translations[lang]["profile"];
}
