// –û—Ç–∫—Ä—ã—Ç—å/–∑–∞–∫—Ä—ã—Ç—å –æ–∫–Ω–æ –ø—Ä–æ—Ñ–∏–ª—è
function toggleProfile() {
    let profile = document.getElementById('profile-popup');
    profile.style.display = profile.style.display === 'block' ? 'none' : 'block';
}

// –û—Ç–∫—Ä—ã—Ç—å/–∑–∞–∫—Ä—ã—Ç—å –∫–æ—Ä–∑–∏–Ω—É
function toggleCart() {
    let cart = document.getElementById('cart-popup');
    cart.style.display = cart.style.display === 'block' ? 'none' : 'block';
}

// –ó–∞–∫—Ä—ã—Ç—å –≤—Å–ø–ª—ã–≤–∞—é—â–µ–µ –æ–∫–Ω–æ
function closePopup(id) {
    document.getElementById(id).style.display = 'none';
}

